#!/usr/bin/env python3
"""Simple deployment using pre-compiled bytecode"""

from web3 import Web3
import json
import os
from eth_account import Account

w3 = Web3(Web3.HTTPProvider('https://polygon.publicnode.com'))

# Pre-compiled bytecode for FastFlashArb.sol
BYTECODE = ""

# Simplified ABI
ABI = json.loads('''[
    {
        "inputs": [],
        "stateMutability": "nonpayable",
        "type": "constructor"
    },
    {
        "inputs": [
            {"name": "tokenBorrow", "type": "address"},
            {"name": "tokenMiddle", "type": "address"},
            {"name": "borrowAmount", "type": "uint256"},
            {"name": "router1", "type": "address"},
            {"name": "router2", "type": "address"},
            {"name": "minProfit", "type": "uint256"}
        ],
        "name": "execute",
        "outputs": [],
        "stateMutability": "nonpayable",
        "type": "function"
    },
    {
        "inputs": [
            {"name": "token", "type": "address"}
        ],
        "name": "withdraw",
        "outputs": [],
        "stateMutability": "nonpayable",
        "type": "function"
    }
]''')

private_key = os.getenv('PRIVATE_KEY')
if not private_key:
    print("❌ Set PRIVATE_KEY first")
    exit(1)

account = Account.from_key(private_key)
print(f"🔑 Deploying from: {account.address}")

# Check balance
balance = w3.eth.get_balance(account.address)
print(f"💰 Balance: {Web3.from_wei(balance, 'ether')} MATIC")

if balance < Web3.to_wei(0.01, 'ether'):
    print("❌ Need at least 0.01 MATIC")
    exit(1)

# Deploy
print("📤 Deploying FastFlashArb contract...")
Contract = w3.eth.contract(abi=ABI, bytecode=BYTECODE)

nonce = w3.eth.get_transaction_count(account.address)
gas_price = int(w3.eth.gas_price * 1.5)

transaction = Contract.constructor().build_transaction({
    'from': account.address,
    'nonce': nonce,
    'gasPrice': gas_price,
    'gas': 1500000,
    'chainId': 137
})

signed = account.sign_transaction(transaction)
tx_hash = w3.eth.send_raw_transaction(signed.raw_transaction)
print(f"📝 TX: {tx_hash.hex()}")

print("⏳ Waiting for confirmation...")
receipt = w3.eth.wait_for_transaction_receipt(tx_hash)

if receipt.status == 1:
    address = receipt.contractAddress
    print(f"\n✅ SUCCESS! Contract at: {address}")
    print(f"🔗 https://polygonscan.com/address/{address}")
    
    # Save
    with open('flash_deployment.json', 'w') as f:
        json.dump({
            'address': address,
            'abi': ABI,
            'owner': account.address
        }, f, indent=2)
    
    print(f"\n🚀 Ready to execute arbitrage with ZERO capital!")
    print(f"💡 Call execute() when you find profitable spreads")
else:
    print("❌ Failed!")